var should_play = songs["sax_medley_with_times"] //.slice(0, 100) 
var has_won = false
var number_correct = 0
var number_incorrect = 0

var playSound = function (url) {
    console.log("trying to play: ", { url })
    const audio = new Audio(url);
    audio.play();
}

function sleep(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
}

function getKeyOctave(key) {
    var octave = key.match(/\d+/)[0]
    key = key.replace(/\d+/, "")
    return [key, octave]
}

function colorTile(key, octave, duration_ms) {
    var elem_with_key = document.getElementsByClassName(key)
    var key_elem = elem_with_key[octave - 2]
    if (key_elem.className.includes("white")) {
        key_elem.style.backgroundColor = "rgb(228, 228, 228)"
        setTimeout(function () {
            key_elem.style.backgroundColor = "white"
        }, duration_ms);
    } else {
        key_elem.style.backgroundColor = "rgb(59, 58, 58)"
        setTimeout(function () {
            key_elem.style.backgroundColor = "black"
        }, duration_ms);
    }
}

async function playAutomatically() {
    for (var i = 0; i < should_play.length; i++) {
        var time_to_wait = should_play[i][1]
        console.log({ time_to_wait })
        var key_with_octave = should_play[i][0]

        if (key_with_octave != "Pause") {
            // var start_time = performance.now()
            playSound("./piano-mp3/" + key_with_octave + ".mp3")
            // console.log("took: ", performance.now() - start_time, "ms")
            var [key, octave] = getKeyOctave(key_with_octave)
            colorTile(key, octave, time_to_wait)
            console.log({ key }, { octave })
            await sleep(time_to_wait) // this is such a fascinating bug!!! that I put it before, and async function! that was ONE cause of it sounding iffy as ** 
            challengeFunc(key, octave)
        } else {
            console.log("actually pausing")
        }
    }
}

// needs the key to have an octave
function playNext(key, id) {
    // console.log({key}, {id})
    var play_number = id.match(/\d+/)[0] - 1
    if (key == "Pause") {
        console.log({ id }, { should_play }, { play_number })
        document.getElementById(id).style.backgroundColor = "white"
        document.getElementById(id).style.height = should_play[number_correct + play_number][1] * 0.2 + "px"
    } else {
        var octave = key.match(/\d+/)[0]
        key = key.replace(/\d+/, "")
        if (key) {
            var c_s = document.getElementsByClassName(key)

            // returns rectangel with rect.top, rect.right, rect.bottom, rect.left
            var left_margin = c_s[octave - 2].getBoundingClientRect().left - 3;

            if (should_play[number_correct][0].includes("b")) {
                left_margin -= 14
            }

            document.getElementById(id).style.marginLeft = left_margin + "px";
            var height = should_play[number_correct + play_number][1] * 0.2 + "px"
            document.getElementById(id).style.height = height
            // return height
        } else {
            document.getElementById(id).style.display = "none"
        }
    }
}

